mapper.select:
SELECT email
FROM MEMBER
WHERE name = #{name} AND birthday = #{birthday, jdbcType=DATE} AND hp = #{hp}

mapper.update:
UPDATE MEMBER SET
pwd = MD5(#{pwd}),
name = #{name}, gender = #{gender}, birthday = #{birthday}, hp = #{hp}, zipcode = #{zipcode}, addr1 = #{addr1}, addr2 = #{addr2} WHERE no = #{no}

UPDATE MEMBER
SET pwd = MD5(#{pwd})
WHERE email = #{email}

UPDATE MEMBER
SET nickname = #{nickname}
WHERE no = #{no}

UPDATE MEMBER SET
seller=1
WHERE no = #{no}

UPDATE MEMBER
SET state = 1
WHERE no = #{no}
