mapper.update:
UPDATE MEMBER SET name = #{name}, nickname = #{nickname}, email = #{email},
pwd = MD5(#{newpwd}),
gender = #{gender}, birthday = #{birthday}, hp = #{hp}, pwd = MD5(#{pwd}), zipcode = #{zipcode}, addr1 = #{addr1}, addr2 = #{addr2}
, profile_org=#{profile_org} , profile_real=#{profile_real}
WHERE no = #{no}
