mapper.insert:
INSERT INTO REVIEW (
	sellno, writeno, score, title, text, writedate
) VALUES (
	#{no}, #{writeno}, #{score}, #{title}, #{text}, NOW() 
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE SELLLIST SET 
	rating_avg=(SELECT AVG(score) FROM REVIEW WHERE sellno=#{no}), review_cnt=(SELECT COUNT(*) FROM REVIEW WHERE sellno=#{no})
WHERE sellno=#{no};
